        function parseLogLine($line) {
            $result = [
                    'date'       => '',
                            'fullname'   => '',
                                    'operation'  => '',
                                            'vote_count' => '',
                                                    'center'     => '',
                                                            'station'    => '',
                                                                    'candidate'  => ''
                                                                        ];

                                                                            $patternAdd = '/^(?<date>\d{4}-\d{2}-\d{2} \d{2}:\d{2}:\d{2}) - (?<fullname>.*?) أضاف (?<vote_count>\d+) صوت(?: في)? في المركز (?<center>.*?) في المحطة (?<station>\d+) إلى المرشح (?<candidate>.*)$/u';
                                                                                $patternDel = '/^(?<date>\d{4}-\d{2}-\d{2} \d{2}:\d{2}:\d{2}) - (?<fullname>.*?) حذف الأصوات من المركز (?<center>.*?) في المحطة (?<station>\d+) للمرشح (?<candidate>.*)$/u';

                                                                                    if (preg_match($patternAdd, $line, $matches)) {
                                                                                            $result['date']       = $matches['date'];
                                                                                                    $result['fullname']   = $matches['fullname'];
                                                                                                            $result['operation']  = 'إضافة';
                                                                                                                    $result['vote_count'] = $matches['vote_count'];
                                                                                                                            $result['center']     = $matches['center'];
                                                                                                                                    $result['station']    = $matches['station'];
                                                                                                                                            $result['candidate']  = $matches['candidate'];
                                                                                                                                                } elseif (preg_match($patternDel, $line, $matches)) {
                                                                                                                                                        $result['date']       = $matches['date'];
                                                                                                                                                                $result['fullname']   = $matches['fullname'];
                                                                                                                                                                        $result['operation']  = 'حذف';
                                                                                                                                                                                $result['vote_count'] = '-';
                                                                                                                                                                                        $result['center']     = $matches['center'];
                                                                                                                                                                                                $result['station']    = $matches['station'];
                                                                                                                                                                                                        $result['candidate']  = $matches['candidate'];
                                                                                                                                                                                                            } else {
                                                                                                                                                                                                                    $result['date']       = 'غير محدد';
                                                                                                                                                                                                                            $result['fullname']   = 'غير محدد';
                                                                                                                                                                                                                                    $result['operation']  = 'غير محدد';
                                                                                                                                                                                                                                            $result['vote_count'] = 'غير محدد';
                                                                                                                                                                                                                                                    $result['center']     = 'غير محدد';
                                                                                                                                                                                                                                                            $result['station']    = 'غير محدد';
                                                                                                                                                                                                                                                                    $result['candidate']  = $line;
                                                                                                                                                                                                                                                                        }
                                                                                                                                                                                                                                                                            return $result;
                                                                                                                                                                                                                                                                            }

                                                                                                                                                                                                                                                                            $logsArray = [];
                                                                                                                                                                                                                                                                            if (file_exists('logs.txt')) {
                                                                                                                                                                                                                                                                                $lines = file('logs.txt', FILE_IGNORE_NEW_LINES | FILE_SKIP_EMPTY_LINES);
                                                                                                                                                                                                                                                                                    usort($lines, function($a, $b) {
                                                                                                                                                                                                                                                                                            $dateA = substr($a, 0, 19);
                                                                                                                                                                                                                                                                                                    $dateB = substr($b, 0, 19);
                                                                                                                                                                                                                                                                                                            return strtotime($dateB) - strtotime($dateA);
                                                                                                                                                                                                                                                                                                                });
                                                                                                                                                                                                                                                                                                                    foreach ($lines as $line) {
                                                                                                                                                                                                                                                                                                                            $logsArray[] = parseLogLine($line);
                                                                                                                                                                                                                                                                                                                                }
                                                                                                                                                                                                                                                                                                                                }
